            dynamicContent.innerHTML = '<p><em>蜉ｨ諤∫函謌千噪蜀・ｮｹ蟾ｲ蜉霓ｽ</em></p>';
            dynamicContent.style.cssText = 'background: #f0f8f0; padding: 10px; margin: 10px 0; border-left: 4px solid #28a745;';

            const mainContent = document.querySelector('main');
